                      Huỷ
                    </button>
                  </td>
                </tr>


              )
            })}
          </tbody>
        </table>

      </div>
    )
  }
}

const mapStateToProps = (rootReducers) => {
  return {
    danhsachGheDangDat: rootReducers.btDatVe.danhsachGheDangDat
  }
}
const mapDispatchToProps =(dispatch) =>{
  return {
      huyGhe: (soGhe) =>{
          dispatch ({
              type: 'HUY_GHE',
              soGhe,
          })
      }

  }
}

