import React, { Component } from 'react'
import {connect} from 'react-redux';
class HangGhe extends Component {
  renderGhe = () =>{
    return this.props.hangGhe.danhSachGhe.map((ghe,index)=>{
      // if (index === 0) {
      //   return <span className="rowNumber">{ghe.soGhe}</span>
      // }
      let cssGheDaDat = '';
      let disabled = false;

      if(ghe.daDat){
        cssGheDaDat = 'gheDuocChon';
        disabled = true;
      }

      let indexGheDangDat = this.props.danhSachGheDangDat.fi
      return <button className={`ghe ${cssGheDaDat}`}key={index} disabled={disabled}>
        {ghe.soGhe}
      </button>
    })
  }

  renderSoHangGhe =()=>{
    return this.props.hangGhe.danhSachGhe.map((hang,index)=>{
      return <button className="rowNumber">
        {hang.soGhe}
      </button>

    })
  }
  renderHangGhe =()=>{
    if(this.props.soHangGhe === 0){
     return <div>
        {this.props.hangGhe.hang} {this.renderSoHangGhe()}

     </div>
    }else{
      return <div className="">
        {this.props.hangGhe.hang} {this.renderGhe()}
      </div>
    }

  }
  render() {
    return (
      <div className='text-left mt-2'>
        {this.renderHangGhe()}
      </div>
    )
  }
}
const mapStateToProps = state =>{
  return {

    danhSachGheDangDat: state.BaiTapDatVeReducer.danhSachGheDangDat
  }


}
export default connect (mapStateToProps)(HangGhe);
